import { useState,useMemo } from "react";


// @mui material components
import Card from "@mui/material/Card";


// Material Dashboard 2 React components
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";
import MDInput from "components/MDInput";
import MDDropDown from "components/MDDropDown"
import TimezoneSelect from 'react-timezone-select'
import InputLabel from '@mui/material/InputLabel';
import MenuItem from '@mui/material/MenuItem';
import FormControl from '@mui/material/FormControl';
import Select from '@mui/material/Select';

// Authentication layout components
import BasicLayout from "layouts/Profile/Component/BasicLayout";


function Basic() {
    const [selectedTimezone, setSelectedTimezone] = useState({})
    const [getLanguage,setLanguage] = useState()
    const handleChange = (event) => {
        setLanguage(event.target.value);
      };

  return (
    <BasicLayout>
      <Card>
        <MDBox
          variant="gradient"
          bgColor="info"
          borderRadius="lg"
          coloredShadow="info"
          mx={2}
          mt={-3}
          p={2}
          mb={1}
          textAlign="center"
        >
          <MDTypography variant="h4" fontWeight="medium" color="white" mt={1}>
            Manage Profile
          </MDTypography>

        </MDBox>
        <MDBox pt={4} pb={3} px={3}>
          <MDBox component="form" role="form">
            <MDBox mb={2}>
              <MDInput type="email" label="Email" fullWidth/>
            </MDBox>
            <MDBox mb={2}>
              <MDInput type="text" label="Display Name" fullWidth/>
            </MDBox>
            <MDBox mb={2}>
              <MDInput type="password" label="Password" fullWidth/>
            </MDBox>
            <MDBox mb={2}>
              <MDInput type="password" label="Confirm Password" fullWidth/>
            </MDBox>
             <MDBox mb={2}>
                <MDTypography  mb={1}
                 fontWeight="regular"
                 fontSize='1rem'
                  color="text">
                    Time Zone
                </MDTypography>
             <TimezoneSelect
              value={selectedTimezone}
               onChange={setSelectedTimezone}
        />
             </MDBox>
          <MDBox mb={2}>
          <FormControl fullWidth>
        <InputLabel id="demo-simple-select-label">Language</InputLabel>
        <Select
          labelId="demo-simple-select-label"
          id="demo-simple-select"
          value={getLanguage}
          label="Language"
          onChange={handleChange}
        >
          <MenuItem value={0}>English (United State)</MenuItem>
          <MenuItem value={1}>Spanish (Latin America)</MenuItem>
          <MenuItem value={2}>Portuguese (Brazil)</MenuItem>
          <MenuItem value={3}>Chinese (Simplified)</MenuItem>
        </Select>
      </FormControl>
          </MDBox>
          </MDBox>
        </MDBox>
      </Card>
    </BasicLayout>
  );
}

export default Basic;
