import { useState } from "react";

// react-router-dom components
import { Link } from "react-router-dom";

// @mui material components
import Card from "@mui/material/Card";
import Grid from "@mui/material/Grid";


// Material Dashboard 2 React components
import MDBox from "components/MDBox";
import MDTypography from "components/MDTypography";
import MDInput from "components/MDInput";
import MDButton from "components/MDButton";
import MDDropDown from "components/MDDropDown"
import TimezoneSelect from 'react-timezone-select'
import { createTheme, ThemeProvider, useTheme } from '@mui/material/styles';
import * as locales from '@mui/material/locale';

// Authentication layout components
import BasicLayout from "layouts/Profile/Component/BasicLayout";


function Basic() {
    const [selectedTimezone, setSelectedTimezone] = useState({})
    const [locale, setLocale] = React.useState('zhCN');

    const theme = useTheme();

    const themeWithLocale = React.useMemo(
      () => createTheme(theme, locales[locale]),
      [locale, theme],
    );


  return (
    <BasicLayout>
      <Card>
        <MDBox
          variant="gradient"
          bgColor="info"
          borderRadius="lg"
          coloredShadow="info"
          mx={2}
          mt={-3}
          p={2}
          mb={1}
          textAlign="center"
        >
          <MDTypography variant="h4" fontWeight="medium" color="white" mt={1}>
            Manage Profile
          </MDTypography>

        </MDBox>
        <MDBox pt={4} pb={3} px={3}>
          <MDBox component="form" role="form">
            <MDBox mb={2}>
              <MDInput type="email" label="Email" fullWidth/>
            </MDBox>
            <MDBox mb={2}>
              <MDInput type="text" label="Display Name" fullWidth/>
            </MDBox>
            <MDBox mb={2}>
              <MDInput type="password" label="Password" fullWidth/>
            </MDBox>
            <MDBox mb={2}>
              <MDInput type="password" label="Confirm Password" fullWidth/>
            </MDBox>
             <MDBox mb={2}>
                <MDTypography  mb={1}
                 fontWeight="regular"
                 fontSize='1rem'
                  color="text">
                    Time Zone
                </MDTypography>
             <TimezoneSelect
              value={selectedTimezone}
               onChange={setSelectedTimezone}
        />
             </MDBox>
             <Box sx={{ width: '100%' }}>
      <ThemeProvider theme={themeWithLocale}>
        <Autocomplete
          options={Object.keys(locales)}
          getOptionLabel={(key) => `${key.substring(0, 2)}-${key.substring(2, 4)}`}
          style={{ width: 300 }}
          value={locale}
          disableClearable
          onChange={(event, newValue) => {
            setLocale(newValue);
          }}

        />
        <TablePagination
          count={2000}
          rowsPerPage={10}
          page={1}
          component="div"
          onPageChange={() => {}}
        />
      </ThemeProvider>
    </Box>
          </MDBox>
        </MDBox>
      </Card>
    </BasicLayout>
  );
}

export default Basic;
